#include <iostream>
#include <string>
#include <algorithm>

int main() {
    std::string grid[2];
    for (int i = 0; i < 2; i++) {
        std::cin >> grid[i];
    }

    std::string rotatedGrid[2];
    for (int i = 0; i < 2; i++) {
        for (int j = 2; j >= 0; j--) {
            rotatedGrid[i] += grid[1 - i][j];
        }
    }

    std::string originalGrid;
    for (int i = 0; i < 2; i++) {
        for (int j = 0; j < 3; j++) {
            originalGrid += grid[i][j];
        }
    }

    std::string rotatedGridStr;
    for (int i = 0; i < 2; i++) {
        rotatedGridStr += rotatedGrid[i];
    }

    if (originalGrid == rotatedGridStr) {
        std::cout << "YES" << std::endl;
    } else {
        std::cout << "NO" << std::endl;
    }

    return 0;
}
