#include <stdio.h>
#include <stdlib.h>

// Function to check if a time is within a given time zone
int is_time_in_zone(int start, int end, int time) {
    return (time >= start && time <= end);
}

// Function to check if a time is within all three time zones
int is_time_in_all_zones(int ast, int aet, int hst, int het, int bst, int bet, int time) {
    return (is_time_in_zone(ast, aet, time) &&
            is_time_in_zone(hst, het, time) &&
            is_time_in_zone(bst, bet, time));
}

int main() {
    int N;
    scanf("%d", &N);

    int max_served = 0;

    for (int i = 0; i < N; i++) {
        int ast, aet, hst, het, bst, bet;
        scanf("%d %d %d %d %d %d %d %d %d %d", &ast, &aet, &hst, &het, &bst, &bet, &ast, &aet, &hst, &het);

        int served = 0;
        for (int j = 0; j < 24; j++) {
            for (int k = 0; k < 60; k++) {
                int time = j * 60 + k;
                if (is_time_in_all_zones(ast, aet, hst, het, bst, bet, time)) {
                    served++;
                }
            }
        }

        max_served = (served > max_served) ? served : max_served;
    }

    printf("%d\n", max_served);

    return 0;
}
