import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        long n = scanner.nextLong();
        long i = (long) Math.sqrt(n);
        while (i * i > n) {
            i--;
        }
        System.out.println(i * i);
    }
}
