import java.util.Arrays;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int[] a = new int[n];
        int[] b = new int[n];
        int[] c = new int[n];

        for (int i = 0; i < n; i++) {
            a[i] = scanner.nextInt();
        }
        for (int i = 0; i < n; i++) {
            b[i] = scanner.nextInt();
        }
        for (int i = 0; i < n; i++) {
            c[i] = scanner.nextInt();
        }

        Arrays.sort(a);
        Arrays.sort(b);
        Arrays.sort(c);

        long ans = 0;
        for (int i = 0; i < n; i++) {
            int j = Arrays.binarySearch(b, a[i]);
            if (j < 0) j = -j - 1;
            int k = Arrays.binarySearch(c, b[i]);
            if (k < 0) k = -k - 1;
            ans += Math.max(0, j - i) * Math.max(0, k - i);
        }

        System.out.println(ans);
    }
}
