#include <iostream>
#include <vector>
#include <algorithm>

struct Time {
    int h, m;
};

bool compare(const Time& a, const Time& b) {
    if (a.h != b.h) return a.h < b.h;
    return a.m < b.m;
}

int main() {
    int N;
    std::cin >> N;

    std::vector<Time> ast(N), aet(N), hst(N), het(N), bst(N), bet(N);

    for (int i = 0; i < N; ++i) {
        std::cin >> ast[i].h >> ast[i].m >> aet[i].h >> aet[i].m
                 >> hst[i].h >> hst[i].m >> het[i].h >> het[i].m
                 >> bst[i].h >> bst[i].m >> bet[i].h >> bet[i].m;
    }

    for (int i = 0; i < N; ++i) {
        ast[i].h = (ast[i].h + 24) % 24;
        aet[i].h = (aet[i].h + 24) % 24;
        hst[i].h = (hst[i].h + 24) % 24;
        het[i].h = (het[i].h + 24) % 24;
        bst[i].h = (bst[i].h + 24) % 24;
        bet[i].h = (bet[i].h + 24) % 24;
    }

    std::sort(ast.begin(), ast.end(), compare);
    std::sort(aet.begin(), aet.end(), compare);
    std::sort(hst.begin(), hst.end(), compare);
    std::sort(het.begin(), het.end(), compare);
    std::sort(bst.begin(), bst.end(), compare);
    std::sort(bet.begin(), bet.end(), compare);

    int ans = 0;
    int i = 0, j = 0, k = 0;

    while (i < N && j < N && k < N) {
        if (ast[i].h <= aet[j].h && ast[i].h <= hst[k].h) {
            if (aet[j].h <= het[k].h && aet[j].h <= bet[k].h) {
                if (hst[k].h <= bst[k].h && hst[k].h <= bet[k].h) {
                    ans++;
                    i++;
                    j++;
                    k++;
                }
                else {
                    k++;
                }
            }
            else {
                j++;
            }
        }
        else {
            i++;
        }
    }

    std::cout << ans << std::endl;

    return 0;
}
