#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 100005

typedef struct {
    int a, b, c;
} part;

int cmp(const void *a, const void *b) {
    part *p1 = (part *)a;
    part *p2 = (part *)b;
    return p1->a - p2->a;
}

int main() {
    int n;
    scanf("%d", &n);
    part p[n];
    for (int i = 0; i < n; i++) {
        scanf("%d", &p[i].a);
    }
    for (int i = 0; i < n; i++) {
        scanf("%d", &p[i].b);
    }
    for (int i = 0; i < n; i++) {
        scanf("%d", &p[i].c);
    }
    qsort(p, n, sizeof(part), cmp);
    long long ans = 0;
    for (int i = 0; i < n; i++) {
        int j = i;
        while (j < n && p[j].b > p[i].a) j++;
        int k = j;
        while (k < n && p[k].c > p[j].b) k++;
        ans += (long long)(j - i) * (k - j);
    }
    printf("%lld\n", ans);
    return 0;
}
