import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int[][] timeZones = new int[n][6];

        for (int i = 0; i < n; i++) {
            for (int j = 0; j < 6; j++) {
                timeZones[i][j] = scanner.nextInt();
            }
        }

        int count = 0;
        for (int i = 0; i < n; i++) {
            boolean canServe = true;
            for (int j = 0; j < n; j++) {
                if (i != j) {
                    if (timeZones[i][0] <= timeZones[j][0] && timeZones[i][1] >= timeZones[j][1]) {
                        canServe = false;
                        break;
                    }
                    if (timeZones[i][2] <= timeZones[j][2] && timeZones[i][3] >= timeZones[j][3]) {
                        canServe = false;
                        break;
                    }
                    if (timeZones[i][4] <= timeZones[j][4] && timeZones[i][5] >= timeZones[j][5]) {
                        canServe = false;
                        break;
                    }
                }
            }
            if (canServe) {
                count++;
            }
        }

        System.out.println(count);
    }
}
