import java.util.*;
import java.io.*;

public class Main {
    static PrintWriter out = new PrintWriter(System.out);
    static FastReader sc = new FastReader();

    public static void main(String[] args) {
        int t = 1;
        while (t-- > 0) {
            solve();
        }
        out.close();
    }

    static void solve() {
        int k = sc.nextInt();
        int n = sc.nextInt();
        long x = 1;
        for (int i = 1; i <= n; i++) {
            x += C(k + i - 1, i);
        }
        long mid = (x + 1) / 2;
        int[] ans = new int[(int) x];
        int idx = 0;
        for (int i = 1; i <= n; i++) {
            int[] temp = new int[(int) C(k + i - 1, i)];
            for (int j = 0; j < temp.length; j++) {
                temp[j] = j + 1;
            }
            for (int j = 0; j < temp.length; j++) {
                if (idx + C(k + i - 1, i) - 1 >= mid) {
                    break;
                }
                ans[idx++] = temp[j];
            }
            for (int j = 1; j <= i - 1; j++) {
                ans[idx++] = 1;
            }
        }
        for (int i = 0; i < ans.length; i++) {
            if (i > 0) out.print(" ");
            out.print(ans[i]);
        }
        out.println();
    }

    static long C(long n, long r) {
        if (r > n - r) r = n - r;
        long ans = 1;
        for (long i = 1; i <= r; i++) {
            ans = ans * (n - i + 1) / i;
        }
        return ans;
    }

    static class FastReader {
        BufferedReader br;
        StringTokenizer st;

        public FastReader() {
            br = new BufferedReader(new InputStreamReader(System.in));
        }

        String next() {
            while (st == null || !st.hasMoreElements()) {
                try {
                    st = new StringTokenizer(br.readLine());
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
            return st.nextToken();
        }

        int nextInt() {
            return Integer.parseInt(next());
        }

        long nextLong() {
            return Long.parseLong(next());
        }

        double nextDouble() {
            return Double.parseDouble(next());
        }

        String nextLine() {
            String str = "";
            try {
                str = br.readLine();
            } catch (IOException e) {
                e.printStackTrace();
            }
            return str;
        }
    }
}
