#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MOD 998244353
#define MAXN 4000

typedef struct {
    int n;
    int x;
    int a[MAXN];
} Input;

typedef struct {
    int n;
    int x;
    int a[MAXN];
    int dp[1 << MAXN];
} State;

int power(int a, int b) {
    int res = 1;
    while (b) {
        if (b & 1) res = (long long)res * a % MOD;
        a = (long long)a * a % MOD;
        b >>= 1;
    }
    return res;
}

int main() {
    Input in;
    scanf("%d %s", &in.n, in.x);
    for (int i = 0; i < in.n; i++) {
        scanf("%s", in.a[i]);
    }

    State st;
    st.n = in.n;
    st.x = in.x;
    for (int i = 0; i < in.n; i++) {
        st.a[i] = in.a[i];
    }

    for (int i = 0; i < (1 << MAXN); i++) {
        st.dp[i] = 0;
    }

    for (int i = 0; i < in.n; i++) {
        st.dp[st.a[i]] = 1;
    }

    for (int i = 0; i < MAXN; i++) {
        for (int j = 0; j < (1 << MAXN); j++) {
            if (j & (1 << i)) {
                st.dp[j] = (st.dp[j] + st.dp[j ^ (1 << i)]) % MOD;
            }
        }
    }

    int ans = 0;
    for (int i = 0; i < (1 << MAXN); i++) {
        if (i <= st.x) {
            ans = (ans + st.dp[i]) % MOD;
        }
    }

    printf("%d\n", ans);

    return 0;
}
