#include <iostream>
#include <vector>
#include <bitset>
#include <algorithm>
#include <numeric>

using namespace std;

const int MOD = 998244353;

int main() {
    int n;
    string x;
    cin >> n >> x;

    vector<int> a(n);
    for (int i = 0; i < n; ++i) {
        cin >> a[i];
    }

    int m = x.size();
    vector<int> p(m + 1);
    p[0] = 1;
    for (int i = 1; i <= m; ++i) {
        p[i] = (p[i - 1] * 2) % MOD;
    }

    vector<int> cnt(m + 1);
    for (int i = 0; i < n; ++i) {
        cnt[x.size() - 1 - __builtin_clz(a[i] + 1)]++;
    }

    vector<int> dp(m + 1);
    dp[0] = 1;
    for (int i = 0; i < m; ++i) {
        for (int j = 0; j < i; ++j) {
            if ((x[i] - '0') ^ (x[j] - '0')) {
                dp[i + 1] = (dp[i + 1] + dp[j] * cnt[i]) % MOD;
            }
        }
        dp[i + 1] = (dp[i + 1] + p[i] * cnt[i]) % MOD;
    }

    int ans = 0;
    for (int i = 0; i <= m; ++i) {
        ans = (ans + dp[i]) % MOD;
    }

    cout << ans << endl;

    return 0;
}
