import java.io.*;
import java.util.*;

public class Main {
    static final int MOD = 998244353;
    static int N, X;
    static int[] A;
    static int[] cnt;
    static int[] dp;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        N = Integer.parseInt(st.nextToken());
        X = Integer.parseInt(st.nextToken(), 2);
        A = new int[N];
        for (int i = 0; i < N; i++) {
            A[i] = Integer.parseInt(br.readLine(), 2);
        }
        cnt = new int[4000];
        for (int i = 0; i < N; i++) {
            for (int j = 0; j < 4000; j++) {
                if (((A[i] >> j) & 1) == 1) {
                    cnt[j]++;
                }
            }
        }
        dp = new int[4000];
        dp[0] = 1;
        for (int i = 0; i < 4000; i++) {
            if (cnt[i] > 0) {
                for (int j = 0; j < 4000; j++) {
                    if (dp[j] > 0) {
                        dp[j] = (int) ((long) dp[j] * (cnt[i] + 1) % MOD);
                    }
                }
            }
            for (int j = 0; j < 4000; j++) {
                if (dp[j] > 0 && dp[j ^ (1 << i)] == 0) {
                    dp[j ^ (1 << i)] = dp[j];
                }
            }
        }
        int ans = 0;
        for (int i = 0; i < 4000; i++) {
            if (dp[i] > 0) {
                ans = (int) ((long) ans + dp[i]) % MOD;
            }
        }
        System.out.println(ans);
    }
}
