import sys
input = sys.stdin.readline

MOD = 998244353

def add(a, b):
    return (a + b) % MOD

def sub(a, b):
    return (a - b + MOD) % MOD

def mul(a, b):
    return (a * b) % MOD

def pow(a, b):
    res = 1
    while b:
        if b & 1:
            res = mul(res, a)
        a = mul(a, a)
        b >>= 1
    return res

def solve():
    N, X = map(int, input().split())
    A = [int(input()) for _ in range(N)]
    X = bin(X)[2:]
    A = [bin(a)[2:] for a in A]

    dp = [0] * (len(X) + 1)
    dp[0] = 1

    for i in range(len(X)):
        for j in range(i):
            if X[j] == X[i]:
                dp[i] = add(dp[i], dp[j])
        dp[i] = add(dp[i], 1)

    for i in range(len(X)):
        for j in range(i):
            if X[j] == X[i]:
                dp[i] = sub(dp[i], dp[j])

    for i in range(len(X)):
        for j in range(i):
            if X[j] != X[i]:
                dp[i] = add(dp[i], dp[j])

    ans = 0
    for i in range(len(X)):
        if X[i] == '1':
            ans = add(ans, dp[i])

    for a in A:
        a = bin(a)[2:]
        cur = 0
        for i in range(len(X)):
            if a[i] == '1':
                cur ^= (1 << (len(X) - 1 - i))
        ans = add(ans, pow(2, cur))

    print(ans)

if __name__ == '__main__':
    solve()
