#include <stdio.h>

int main() {
    int n, k;
    scanf("%d %d", &n, &k);

    int result = 1;
    for (int i = 0; i < n; i++) {
        if (i % 2 == 0) {
            result *= 2;
        } else {
            result += k;
        }
    }

    printf("%d\n", result);
    return 0;
}
