#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

using namespace std;

bool check(const string& s, const string& t) {
    for (int i = 0; i < t.size(); ++i) {
        if (s[i] != '?' && s[i] != t[i]) return false;
    }
    return true;
}

string solve() {
    string s, t;
    cin >> s >> t;

    int n = s.size();
    int m = t.size();

    vector<string> dp(n + 1, string(m + 1, '?'));

    for (int i = 0; i <= n; ++i) {
        for (int j = 0; j <= m; ++j) {
            if (i == 0 || j == 0) {
                dp[i][j] = '?';
            } else if (s[i - 1] == t[j - 1] || t[j - 1] == '?') {
                dp[i][j] = dp[i - 1][j - 1];
            } else {
                dp[i][j] = '?';
            }
        }
    }

    string ans = "";
    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < m; ++j) {
            if (check(dp[i + 1], t.substr(j, m - j))) {
                ans = s.substr(0, i + 1);
                for (int k = 0; k < m - j; ++k) {
                    ans += t[j + k];
                }
                ans += s.substr(i + 1);
                return ans;
            }
        }
    }

    return "UNRESTORABLE";
}

int main() {
    cout << solve() << endl;
    return 0;
}
