#include <stdio.h>
#include <stdlib.h>

int main() {
    int W, H;
    scanf("%d %d", &W, &H);

    int **grid = (int **)malloc(H * sizeof(int *));
    for (int i = 0; i < H; i++) {
        grid[i] = (int *)malloc(W * sizeof(int));
    }

    for (int i = 0; i < H; i++) {
        for (int j = 0; j < W; j++) {
            scanf("%d", &grid[i][j]);
        }
    }

    int *row_counts = (int *)malloc(H * sizeof(int));
    int *col_counts = (int *)malloc(W * sizeof(int));

    for (int i = 0; i < H; i++) {
        row_counts[i] = 0;
        for (int j = 0; j < W; j++) {
            row_counts[i] += grid[i][j];
        }
    }

    for (int j = 0; j < W; j++) {
        col_counts[j] = 0;
        for (int i = 0; i < H; i++) {
            col_counts[j] += grid[i][j];
        }
    }

    int row_odd = 0;
    int row_even = 0;
    int col_odd = 0;
    int col_even = 0;

    for (int i = 0; i < H; i++) {
        if (row_counts[i] % 2 == 1) {
            row_odd++;
        } else {
            row_even++;
        }
    }

    for (int j = 0; j < W; j++) {
        if (col_counts[j] % 2 == 1) {
            col_odd++;
        } else {
            col_even++;
        }
    }

    if (row_odd == 1 && col_odd == 1) {
        printf("yes\n");
    } else if (row_odd == 0 && col_odd == 0) {
        printf("yes\n");
    } else {
        printf("no\n");
    }

    for (int i = 0; i < H; i++) {
        free(grid[i]);
    }
    free(grid);
    free(row_counts);
    free(col_counts);

    return 0;
}
