#include <stdio.h>
#include <math.h>

int main() {
    int N;
    scanf("%d", &N);

    int t[N], v[N];
    for (int i = 0; i < N; i++) {
        scanf("%d", &t[i]);
    }
    for (int i = 0; i < N; i++) {
        scanf("%d", &v[i]);
    }

    double total_distance = 0;
    for (int i = 0; i < N; i++) {
        double a = 1;
        double s = 0.5 * a * t[i] * t[i];
        double v_max = a * t[i];
        double d = v_max * t[i] - 0.5 * a * t[i] * t[i];
        total_distance += d;
        if (i < N - 1) {
            double t_next = t[i + 1];
            double v_next = v[i + 1];
            double t_decel = (v_next - v_max) / a;
            if (t_decel > 0 && t_decel < t_next) {
                double d_decel = 0.5 * a * t_decel * t_decel;
                total_distance += d_decel;
                t_next -= t_decel;
                v_max = v_next;
            }
            double t_const = t_next - t_decel;
            total_distance += v_max * t_const;
        }
    }

    printf("%.6lf\n", total_distance);
    return 0;
}
