#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    int N;
    cin >> N;

    vector<int> t(N);
    vector<int> v(N);

    for (int i = 0; i < N; i++) {
        cin >> t[i];
    }

    for (int i = 0; i < N; i++) {
        cin >> v[i];
    }

    double totalDistance = 0;

    for (int i = 0; i < N; i++) {
        double a = 1;
        double v0 = 0;
        double t0 = 0;
        double t1 = t[i];

        double distance = 0;

        if (i == 0) {
            distance = 0.5 * a * pow(t1, 2) + v0 * t1;
        } else {
            distance = 0.5 * a * pow(t1, 2) + v0 * t1;
            v0 = v[i - 1];
        }

        totalDistance += distance;

        if (i == N - 1) {
            break;
        }

        a = -1;
        v0 = v[i];
        t0 = t[i];

        distance = 0;

        if (i == N - 1) {
            distance = 0.5 * a * pow(t1, 2) + v0 * t1;
        } else {
            distance = 0.5 * a * pow(t1, 2) + v0 * t1;
            v0 = v[i + 1];
        }

        totalDistance += distance;
    }

    cout << fixed << setprecision(6) << totalDistance << endl;

    return 0;
}
