import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int[] t = new int[n];
        int[] v = new int[n];
        for (int i = 0; i < n; i++) {
            t[i] = scanner.nextInt();
        }
        for (int i = 0; i < n; i++) {
            v[i] = scanner.nextInt();
        }

        double[] a = new double[n];
        double[] s = new double[n + 1];
        double[] d = new double[n + 1];

        for (int i = 0; i < n; i++) {
            a[i] = 1;
            s[i + 1] = s[i] + 0.5 * a[i] * t[i] * t[i] + v[i] * t[i];
            d[i + 1] = 0.5 * a[i] * t[i] * t[i] + v[i] * t[i];
        }

        double maxDistance = 0;
        for (int i = 0; i < n; i++) {
            double distance = 0;
            for (int j = 0; j <= i; j++) {
                distance += d[j + 1];
            }
            for (int j = i + 1; j < n; j++) {
                distance += d[n] - d[j + 1];
            }
            maxDistance = Math.max(maxDistance, distance);
        }

        System.out.printf("%.3f%n", maxDistance);
    }
}
