import sys

def calculate_distance(t, v):
    distance = 0
    for i in range(len(t)):
        if i == 0:
            distance += 0.5 * (v[i] ** 2) - 0.5 * (0 ** 2)
        elif i == len(t) - 1:
            distance += 0.5 * (v[i] ** 2) - 0.5 * (v[i-1] ** 2)
        else:
            distance += v[i] * t[i]
    return distance

def main():
    n = int(input())
    t = list(map(int, input().split()))
    v = list(map(int, input().split()))

    max_distance = 0
    for i in range(n):
        distance = 0
        for j in range(i, n):
            if j == i:
                distance += 0.5 * (v[j] ** 2) - 0.5 * (0 ** 2)
            elif j == n - 1:
                distance += 0.5 * (v[j] ** 2) - 0.5 * (v[j-1] ** 2)
            else:
                distance += v[j] * t[j]
            if j == n - 1:
                max_distance = max(max_distance, distance)
            else:
                distance -= 0.5 * (v[j] ** 2) - 0.5 * (v[j+1] ** 2)

    print(f"{max_distance:.6f}")

if __name__ == "__main__":
    main()
