    int a[n];
    for (int i = 0; i < n; i++) {
        scanf("%d", &a[i]);
    }
    long long int ans = 1;
    for (int i = 0; i < n; i++) {
        if (a[i] % 2 == 0) {
            ans *= 2;
        } else {
            ans *= 2 * (a[i] + 1);
        }
    }
    if (ans % 2 == 0) {
        ans = (ans + 1) / 2;
    } else {
        ans = (ans + 1) / 2;
