#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    int W, H;
    cin >> W >> H;

    vector<vector<int>> grid(H, vector<int>(W));
    for (int i = 0; i < H; i++) {
        for (int j = 0; j < W; j++) {
            cin >> grid[i][j];
        }
    }

    vector<int> row_counts(W, 0);
    vector<int> col_counts(H, 0);

    for (int i = 0; i < H; i++) {
        for (int j = 0; j < W; j++) {
            if (grid[i][j] == 1) {
                row_counts[j]++;
                col_counts[i]++;
            }
        }
    }

    for (int i = 0; i < W; i++) {
        if (row_counts[i] % 2 != 0) {
            cout << "no" << endl;
            return 0;
        }
    }

    for (int i = 0; i < H; i++) {
        if (col_counts[i] % 2 != 0) {
            cout << "no" << endl;
            return 0;
        }
    }

    cout << "yes" << endl;

    return 0;
}
