#include <stdio.h>
#include <string.h>

int main() {
    char s[100001];
    scanf("%s", s);
    int n = strlen(s);
    int l = 0, r = n - 1;
    int cnt = 0;

    while (l < r) {
        if (s[l] == s[r]) {
            l++;
            r--;
        } else {
            cnt++;
            int i;
            for (i = l + 1; i < r; i++) {
                if (s[i] == s[r]) {
                    break;
                }
            }
            if (i == r) {
                cnt += r - l;
                break;
            }
            for (i = r - 1; i > l; i--) {
                if (s[i] == s[l]) {
                    break;
                }
            }
            if (i == l) {
                cnt += r - l;
                break;
            }
            cnt++;
        }
    }
    if (l >= r) {
        cnt = (n - l) / 2;
    }
    printf("%d\n", cnt);
    return 0;
}
