import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        String s = scanner.next();
        int n = s.length();
        int[] left = new int[n];
        int[] right = new int[n];

        for (int i = 0; i < n; i++) {
            left[i] = i;
            right[i] = n - i - 1;
        }

        int ans = 0;
        for (int i = 0; i < n / 2; i++) {
            if (s.charAt(i) != s.charAt(n - i - 1)) {
                int j = n - i - 1;
                for (int k = i + 1; k <= j; k++) {
                    if (s.charAt(k) == s.charAt(n - k)) {
                        left[k] = left[k - 1];
                        right[k] = right[k - 1];
                    }
                }
                if (left[j] > right[i]) {
                    ans = -1;
                    break;
                }
                ans += j - i - 1 - (left[j] - right[i] - 1);
                left[j] = right[i] + 1;
                right[i] = left[j] - 1;
            }
        }

        if (ans < 0) {
            System.out.println(ans);
        } else {
            System.out.println(n - ans);
        }
    }
}
