#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define MOD 1000000007

typedef long long ll;

ll gcd(ll a, ll b) {
    if (b == 0) return a;
    return gcd(b, a % b);
}

ll lcm(ll a, ll b) {
    return a / gcd(a, b) * b;
}

ll powmod(ll a, ll b) {
    ll res = 1;
    while (b) {
        if (b & 1) res = res * a % MOD;
        a = a * a % MOD;
        b >>= 1;
    }
    return res;
}

int main() {
    ll A, B, C, D;
    scanf("%lld %lld %lld %lld", &A, &B, &C, &D);
    ll ans = 0;
    for (ll z = 0; z <= C; z++) {
        ll x = 0, y = 0;
        for (ll i = 0; i <= A; i++) {
            for (ll j = 0; j <= B; j++) {
                if (z - D <= i && i <= z + D && j + D - i <= B && j - i + D <= B) {
                    x = i;
                    y = j;
                }
            }
        }
        for (ll i = 0; i <= A; i++) {
            for (ll j = 0; j <= B; j++) {
                if (z - D <= i && i <= z + D && j + D - i <= B && j - i + D <= B) {
                    ll dx = abs(i - x);
                    ll dy = abs(j - y);
                    ll dz = abs(z - (i + j));
                    if (dx <= D && dy <= D && dz <= D) {
                        ans++;
                    }
                }
            }
        }
    }
    printf("%lld\n", ans);
    return 0;
}
