#include <iostream>
#include <vector>
#include <algorithm>
#include <numeric>

using namespace std;

const int MOD = 1e9 + 7;

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int A, B, C, D;
    cin >> A >> B >> C >> D;

    vector<int> divs;
    for (int i = 1; i * i <= A; i++) {
        if (A % i == 0) {
            divs.push_back(i);
            if (i * i != A) {
                divs.push_back(A / i);
            }
        }
    }

    int ans = 0;
    for (int i : divs) {
        for (int j : divs) {
            if (i * j <= D) {
                continue;
            }
            int x = (A + i - 1) / i;
            int y = (B + j - 1) / j;
            int z = (C + (i * j) - 1) / (i * j);
            int cnt = (x - 1) * (y - 1) * (z - 1);
            ans = (ans + cnt) % MOD;
        }
    }

    cout << ans << '\n';

    return 0;
}
