import sys
input = sys.stdin.readline

mod = 10**9 + 7

def add(a, b):
    a += b
    if a >= mod:
        a -= mod
    return a

def sub(a, b):
    a -= b
    if a < 0:
        a += mod
    return a

def mul(a, b):
    return (a * b) % mod

def pow(a, b):
    res = 1
    while b:
        if b & 1:
            res = mul(res, a)
        a = mul(a, a)
        b >>= 1
    return res

def inv(a):
    return pow(a, mod - 2)

def ncr(n, r):
    if r > n:
        return 0
    res = 1
    for i in range(r):
        res = mul(res, n - i)
        res = mul(res, inv(i + 1))
    return res

def solve():
    A, B, C, D = map(int, input().split())
    ans = 0
    for i in range(D + 1):
        for j in range(D + 1):
            for k in range(D + 1):
                if i + j + k <= D:
                    continue
                x = i + j + k
                if x > D:
                    continue
                a = max(0, (D - x) // A + 1)
                b = max(0, (D - x) // B + 1)
                c = max(0, (D - x) // C + 1)
                ans = add(ans, mul(ncr(x + 2, 2), mul(ncr(x + 1, 1), mul(ncr(x, 0), mul(ncr(a, 1), mul(ncr(b, 1), ncr(c, 1)))))))
    print(ans)

t = 1
for _ in range(t):
    solve()
