#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MOD 1000000007

int n;
int a[400], b[400], c[400];
int cnt[400];

int main() {
    scanf("%d", &n);
    for (int i = 0; i < n; i++) {
        scanf("%d", &a[i]);
        a[i]--;
    }
    for (int i = 0; i < n; i++) {
        scanf("%d", &b[i]);
        b[i]--;
    }

    int ans = 1;
    for (int i = 0; i < n; i += 3) {
        int x = a[i], y = a[i + 1], z = a[i + 2];
        int u = b[i], v = b[i + 1], w = b[i + 2];

        if (x == y || y == z || z == x || u == v || v == w || w == u) {
            printf("0\n");
            return 0;
        }

        int cnt[3] = {0};
        cnt[x]++;
        cnt[y]++;
        cnt[z]++;
        cnt[u]++;
        cnt[v]++;
        cnt[w]++;
        int sum = 0;
        for (int j = 0; j < 3; j++) {
            sum += cnt[j];
        }
        int mul = 1;
        for (int j = 0; j < 3; j++) {
            if (cnt[j] == sum - 2) {
                mul *= (sum - 2);
                mul %= MOD;
            }
        }
        ans *= mul;
        ans %= MOD;
    }

    for (int i = 0; i < n; i++) {
        cnt[a[i]]++;
        cnt[b[i]]++;
    }
    int sum = 0;
    for (int i = 0; i < n; i++) {
        sum += cnt[i];
    }
    int mul = 1;
    for (int i = 0; i < n; i++) {
        if (cnt[i] == sum - 2) {
            mul *= (sum - 2);
            mul %= MOD;
        }
    }
    ans *= mul;
    ans %= MOD;

    printf("%d\n", ans);
    return 0;
}
