import java.io.*;
import java.util.*;

public class Main {
    static final int MOD = 1000000007;
    static final int MAXN = 400;

    static int n;
    static int[] a, b, c;
    static int[] cnt;
    static int[] cnt2;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);

        n = Integer.parseInt(br.readLine());
        a = new int[n];
        b = new int[n];
        StringTokenizer st = new StringTokenizer(br.readLine());
        for (int i = 0; i < n; i++) {
            a[i] = Integer.parseInt(st.nextToken()) - 1;
        }
        st = new StringTokenizer(br.readLine());
        for (int i = 0; i < n; i++) {
            b[i] = Integer.parseInt(st.nextToken()) - 1;
        }

        cnt = new int[n];
        cnt2 = new int[n];
        for (int i = 0; i < n; i++) {
            cnt[a[i]]++;
            cnt2[b[i]]++;
        }

        long ans = 0;
        for (int i = 0; i < n; i++) {
            if (cnt[i] == 1 && cnt2[i] == 1) {
                int j = 0;
                while (j < n && cnt[j] == 1) {
                    j++;
                }
                int k = 0;
                while (k < n && cnt2[k] == 1) {
                    k++;
                }
                if (j == n || k == n) {
                    ans++;
                }
            }
        }

        for (int i = 0; i < n; i++) {
            if (cnt[i] == 2 && cnt2[i] == 2) {
                int j = 0;
                while (j < n && cnt[j] == 1) {
                    j++;
                }
                int k = 0;
                while (k < n && cnt2[k] == 1) {
                    k++;
                }
                if (j == n || k == n) {
                    ans++;
                }
            }
        }

        for (int i = 0; i < n; i++) {
            if (cnt[i] == 2 && cnt2[i] == 2) {
                int j = 0;
                while (j < n && cnt[j] == 1) {
                    j++;
                }
                int k = 0;
                while (k < n && cnt2[k] == 1) {
                    k++;
                }
                if (j == n || k == n) {
                    continue;
                }
                int x = 0, y = 0, z = 0;
                for (int p = 0; p < n; p++) {
                    if (p != i && p != j && p != k) {
                        if (cnt[p] == 1) {
                            x = p;
                        } else if (cnt2[p] == 1) {
                            y = p;
                        } else {
                            z = p;
                        }
                    }
                }
                long cur = 1;
                for (int p = 0; p < n; p++) {
                    if (p != i && p != j && p != k) {
                        if (cnt[p] == 1) {
                            cur = (cur * (n - 3)) % MOD;
                        } else if (cnt2[p] == 1) {
                            cur = (cur * (n - 3)) % MOD;
                        } else {
                            cur = (cur * 2) % MOD;
                        }
                    }
                }
                ans = (ans + cur) % MOD;
            }
        }

        pw.println(ans);
        pw.close();
    }
}
