import sys
input = sys.stdin.readline

MOD = 10**9 + 7

def pow_mod(a, b):
    res = 1
    while b:
        if b & 1:
            res = res * a % MOD
        a = a * a % MOD
        b >>= 1
    return res

def inv(a):
    return pow_mod(a, MOD - 2)

def C(n, k):
    return fac[n] * inv(fac[k]) % MOD * inv(fac[n - k]) % MOD

N = int(input())
a = list(map(int, input().split()))
b = list(map(int, input().split()))

fac = [1] * (N + 1)
for i in range(1, N + 1):
    fac[i] = fac[i - 1] * i % MOD

a_cnt = [0] * (N + 1)
b_cnt = [0] * (N + 1)
for i in range(N):
    a_cnt[a[i]] += 1
    b_cnt[b[i]] += 1

a_cnt[0] = 0
b_cnt[0] = 0

a_cnt.sort(reverse=True)
b_cnt.sort(reverse=True)

c_cnt = [0] * (N + 1)
for i in range(N):
    c_cnt[a[i]] += 1
    c_cnt[b[i]] += 1

c_cnt[0] = 0

ans = 0
for i in range(1, N + 1):
    if c_cnt[i] == 0:
        continue
    if c_cnt[i] > 1:
        break
    if a_cnt[i] == 0 or b_cnt[i] == 0:
        continue
    if a_cnt[i] == 1 and b_cnt[i] == 1:
        continue
    if a_cnt[i] == 2 and b_cnt[i] == 2:
        ans = (ans + C(a_cnt[i], 2) * C(b_cnt[i], 2)) % MOD
    elif a_cnt[i] == 2 and b_cnt[i] == 1:
        ans = (ans + C(a_cnt[i], 2) * b_cnt[i]) % MOD
    elif a_cnt[i] == 1 and b_cnt[i] == 2:
        ans = (ans + C(b_cnt[i], 2) * a_cnt[i]) % MOD
    else:
        ans = (ans + a_cnt[i] * b_cnt[i]) % MOD

print(ans)
