#include <stdio.h>
#include <stdlib.h>

#define MAX_H 50
#define MAX_W 50

int main() {
    int H, W;
    char S[MAX_H][MAX_W + 1];

    scanf("%d %d", &H, &W);
    for (int i = 0; i < H; i++) {
        scanf("%s", S[i]);
        S[i][W] = '\0';
    }

    for (int i = 0; i < H; i++) {
        for (int j = 0; j < W; j++) {
            int count = 0;
            for (int x = -1; x <= 1; x++) {
                for (int y = -1; y <= 1; y++) {
                    int ni = i + x;
                    int nj = j + y;
                    if (ni >= 0 && ni < H && nj >= 0 && nj < W) {
                        if (S[ni][nj] == '#') {
                            count++;
                        }
                    }
                }
            }
            if (S[i][j] == '.') {
                S[i][j] = '0' + count;
            }
        }
        printf("%s\n", S[i]);
    }

    return 0;
}
