#include <iostream>
#include <vector>
#include <string>

using namespace std;

int main() {
    int H, W;
    cin >> H >> W;

    vector<string> grid(H);
    for (int i = 0; i < H; i++) {
        cin >> grid[i];
    }

    vector<string> result(H);
    for (int i = 0; i < H; i++) {
        for (int j = 0; j < W; j++) {
            int count = 0;
            for (int x = -1; x <= 1; x++) {
                for (int y = -1; y <= 1; y++) {
                    int nx = i + x;
                    int ny = j + y;
                    if (nx >= 0 && nx < H && ny >= 0 && ny < W) {
                        if (grid[nx][ny] == '#') {
                            count++;
                        }
                    }
                }
            }
            if (grid[i][j] == '.') {
                result[i] += to_string(count);
            } else {
                result[i] += grid[i][j];
            }
        }
    }

    for (const auto& str : result) {
        cout << str << endl;
    }

    return 0;
}
