#include <stdio.h>
#include <string.h>
#include <limits.h>

#define MAX 100000

int min(int a, int b) {
    return (a < b) ? a : b;
}

int max(int a, int b) {
    return (a > b) ? a : b;
}

int cut(int* arr, int n, int k) {
    int max = INT_MIN, min = INT_MAX;
    for (int i = 0; i < k; i++) {
        int sum = 0;
        for (int j = i; j < n; j += k) {
            sum += arr[j];
        }
        max = max(max, sum);
        min = min(min, sum);
    }
    return max - min;
}

int main() {
    char str[MAX + 1];
    int n, min_year = INT_MAX;

    scanf("%s", str);
    n = strlen(str);

    for (int i = 1; i <= n; i++) {
        int* arr = (int*)malloc(n * sizeof(int));
        for (int j = 0; j < n; j++) {
            arr[j] = str[j] - '0';
        }

        int year = cut(arr, n, i);
        free(arr);
        min_year = min(min_year, year);
    }

    printf("%d\n", min_year);

    return 0;
}
