#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 55

typedef struct {
    int x, y;
} Point;

int cmpx(const void *a, const void *b) {
    return ((Point *)a)->x - ((Point *)b)->x;
}

int cmpy(const void *a, const void *b) {
    return ((Point *)a)->y - ((Point *)b)->y;
}

int main() {
    int n, k;
    scanf("%d%d", &n, &k);

    Point p[MAXN];
    for (int i = 0; i < n; i++) {
        scanf("%d%d", &p[i].x, &p[i].y);
    }

    qsort(p, n, sizeof(Point), cmpx);
    int minx = p[0].x, maxx = p[n - 1].x;

    qsort(p, n, sizeof(Point), cmpy);
    int miny = p[0].y, maxy = p[n - 1].y;

    int ans = (maxx - minx) * (maxy - miny);
    for (int i = 0; i < n - k + 1; i++) {
        int x = p[i].x;
        for (int j = i + 1; j < n - k + 1; j++) {
            int y = p[j].y;
            int minx = p[i].x, maxx = p[i + k - 1].x;
            int miny = p[j].y, maxy = p[j + k - 1].y;
            int area = (maxx - minx) * (maxy - miny);
            ans = (area < ans) ? area : ans;
        }
    }

    printf("%d\n", ans);
    return 0;
}
