#include <iostream>
#include <algorithm>
#include <vector>
#include <utility>

using namespace std;

typedef long long ll;
typedef pair<int, int> pii;

int main() {
    int n, k;
    cin >> n >> k;

    vector<pii> points(n);
    for (int i = 0; i < n; i++) {
        cin >> points[i].first >> points[i].second;
    }

    sort(points.begin(), points.end());

    ll min_area = 1e18;
    for (int i = 0; i < n - k + 1; i++) {
        int x1 = points[i].first;
        int y1 = points[i].second;
        int x2 = points[i + k - 1].first;
        int y2 = points[i + k - 1].second;

        int x_min = min(x1, x2);
        int y_min = min(y1, y2);
        int x_max = max(x1, x2);
        int y_max = max(y1, y2);

        ll area = (ll)(x_max - x_min) * (ll)(y_max - y_min);
        min_area = min(min_area, area);
    }

    cout << min_area << endl;

    return 0;
}
