#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

int minYears(const std::string& s) {
    int n = s.size();
    int minDiff = n;

    for (int i = 1; i < n; ++i) {
        std::vector<int> nums;
        for (int j = 0; j < n; j += i) {
            int num = 0;
            for (int k = j; k < j + i && k < n; ++k) {
                num = num * 10 + (s[k] - '0');
            }
            nums.push_back(num);
        }

        int maxNum = *std::max_element(nums.begin(), nums.end());
        int minNum = *std::min_element(nums.begin(), nums.end());
        int diff = maxNum - minNum;
        minDiff = std::min(minDiff, diff);
    }

    return minDiff;
}

int main() {
    std::string s;
    std::cin >> s;
    std::cout << minYears(s) << std::endl;
    return 0;
}
