#include <iostream>
#include <vector>
#include <algorithm>
#include <unordered_map>

using namespace std;

int main() {
    int n, m;
    cin >> n;
    vector<int> d(n);
    for (int i = 0; i < n; i++) {
        cin >> d[i];
    }
    cin >> m;
    vector<int> t(m);
    for (int i = 0; i < m; i++) {
        cin >> t[i];
    }

    sort(d.begin(), d.end());
    sort(t.begin(), t.end());

    unordered_map<int, int> count;
    for (int i = 0; i < n; i++) {
        count[d[i]]++;
    }

    int i = 0, j = 0;
    while (i < m && j < n) {
        if (t[i] == d[j]) {
            i++;
            count[d[j]]--;
        } else if (t[i] < d[j]) {
            cout << "NO" << endl;
            return 0;
        } else {
            j++;
        }
    }

    if (i < m) {
        cout << "NO" << endl;
    } else {
        cout << "YES" << endl;
    }

    return 0;
}
