#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 100005

int n, m;
int a[MAXN], b[MAXN];
int d[MAXN];
int vis[MAXN];
int cnt;

int dfs(int u) {
    vis[u] = 1;
    for (int i = 0; i < cnt; i++) {
        if (d[u] == d[a[i]] + 1 && !vis[b[i]]) {
            vis[b[i]] = 1;
            if (dfs(b[i])) return 1;
        }
    }
    return 0;
}

int main() {
    scanf("%d%d", &n, &m);
    for (int i = 1; i <= m; i++) {
        scanf("%d%d", &a[i], &b[i]);
    }
    for (int i = 1; i <= n; i++) d[i] = -1;
    for (int i = 1; i <= m; i++) {
        if (d[a[i]] == -1) d[a[i]] = 0;
        if (d[b[i]] == -1) d[b[i]] = 0;
        d[a[i]]++;
        d[b[i]]++;
    }
    for (int i = 1; i <= n; i++) {
        if (d[i] == 1) {
            printf("%d\n", n - 1 + m);
            return 0;
        }
    }
    cnt = 0;
    for (int i = 1; i <= m; i++) {
        if (d[a[i]] == 3) {
            a[cnt] = a[i];
            b[cnt] = b[i];
            cnt++;
        }
    }
    int ans = 0;
    for (int i = 1; i <= n; i++) {
        if (!vis[i]) {
            ans += dfs(i);
        }
    }
    printf("%d\n", ans);
    return 0;
}
