#include <iostream>
#include <vector>
#include <queue>
#include <set>
#include <algorithm>

using namespace std;

const int MAX_N = 1e5 + 5;

vector<int> adj[MAX_N];
int dist[MAX_N];
int parent[MAX_N];
int cnt[MAX_N];
int n, m;

void bfs(int s) {
    queue<int> q;
    q.push(s);
    dist[s] = 0;
    while (!q.empty()) {
        int u = q.front();
        q.pop();
        for (int v : adj[u]) {
            if (dist[v] == -1) {
                dist[v] = dist[u] + 1;
                q.push(v);
                parent[v] = u;
            }
        }
    }
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    cin >> n >> m;
    for (int i = 0; i < m; i++) {
        int u, v;
        cin >> u >> v;
        adj[u].push_back(v);
        adj[v].push_back(u);
    }

    for (int i = 1; i <= n; i++) {
        cnt[dist[i]]++;
    }

    int ans = 0;
    for (int i = 1; i <= n; i++) {
        bfs(i);
        for (int j = 1; j <= n; j++) {
            if (dist[j] == 3) {
                cnt[dist[parent[j]]]--;
                cnt[dist[j]]++;
            }
        }
        ans += cnt[3];
        for (int j = 1; j <= n; j++) {
            dist[j] = -1;
            parent[j] = 0;
            cnt[dist[j]] = 0;
            adj[j].clear();
        }
    }

    cout << ans << '\n';

    return 0;
}
