import java.io.*;
import java.util.*;

public class Main {
    static int N, M;
    static int[] A, B;
    static int[] color;
    static int[] depth;
    static int[] parent;
    static int[] size;
    static int[] head;
    static int[] to;
    static int[] next;
    static int cnt;
    static int ans;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st = new StringTokenizer(br.readLine());
        N = Integer.parseInt(st.nextToken());
        M = Integer.parseInt(st.nextToken());
        A = new int[M + 1];
        B = new int[M + 1];
        color = new int[N + 1];
        depth = new int[N + 1];
        parent = new int[N + 1];
        size = new int[N + 1];
        head = new int[N + 1];
        to = new int[2 * M + 1];
        next = new int[2 * M + 1];
        for (int i = 1; i <= M; i++) {
            st = new StringTokenizer(br.readLine());
            A[i] = Integer.parseInt(st.nextToken());
            B[i] = Integer.parseInt(st.nextToken());
            add(A[i], B[i]);
            add(B[i], A[i]);
        }
        dfs(1, 0);
        for (int i = 1; i <= N; i++) {
            if (color[i] == 1) {
                int u = i;
                int v = parent[i];
                while (color[v] != 1) {
                    v = parent[v];
                }
                int w = v;
                while (u != w) {
                    add(u, w);
                    u = parent[u];
                    w = parent[w];
                }
            }
        }
        for (int i = 1; i <= N; i++) {
            if (color[i] == 1) {
                int u = i;
                int v = parent[i];
                while (color[v] != 1) {
                    v = parent[v];
                }
                int w = v;
                while (u != w) {
                    add(u, w);
                    u = parent[u];
                    w = parent[w];
                }
                ans += size[i] - 1;
            }
        }
        pw.println(ans + M - N);
        pw.close();
    }

    static void add(int u, int v) {
        to[++cnt] = v;
        next[cnt] = head[u];
        head[u] = cnt;
    }

    static void dfs(int u, int fa) {
        parent[u] = fa;
        size[u] = 1;
        for (int i = head[u]; i != 0; i = next[i]) {
            int v = to[i];
            if (v == fa) continue;
            depth[v] = depth[u] + 1;
            dfs(v, u);
            size[u] += size[v];
        }
        if (fa == 0) {
            if (size[u] % 2 == 0) color[u] = 1;
            else color[u] = 2;
        } else {
            if (size[u] % 2 == 0) color[u] = color[fa];
            else color[u] = 3 - color[fa];
        }
    }
}
