from collections import deque

def bfs(graph, start):
    visited = set()
    queue = deque([start])
    visited.add(start)
    level = {start: 0}
    while queue:
        node = queue.popleft()
        for neighbor in graph[node]:
            if neighbor not in visited:
                visited.add(neighbor)
                queue.append(neighbor)
                level[neighbor] = level[node] + 1
    return visited, level

def max_edges(n, m, edges):
    graph = {i: set() for i in range(1, n + 1)}
    for u, v in edges:
        graph[u].add(v)
        graph[v].add(u)

    visited, level = bfs(graph, 1)
    max_level = max(level.values())
    if max_level <= 3:
        return m
    else:
        count = 0
        for node in visited:
            if level[node] == 3:
                for neighbor in graph[node]:
                    if level[neighbor] == 2:
                        count += 1
        return m + count // 2

n, m = map(int, input().split())
edges = []
for _ in range(m):
    u, v = map(int, input().split())
    edges.append((u, v))
print(max_edges(n, m, edges))
