#include <iostream>
#include <string>

int main() {
    int n;
    std::string s;
    std::cin >> n >> s;

    int count = 0;
    int i = 0;
    while (i < n - 2) {
        if (s[i] == '1' && s[i + 1] == '0' && s[i + 2] == '1') {
            count++;
            i += 3;
        } else {
            i++;
        }
    }

    std::cout << count << std::endl;

    return 0;
}
