import java.util.Arrays;

public class Main {
    public static void main(String[] args) {
        int n = Integer.parseInt(new String(new char[readInt()]).replace('\0', '0'));
        int min = Integer.MAX_VALUE;
        for (int i = 1; i <= n; i++) {
            int max = 0, minVal = 9;
            for (int j = i; j <= n; j += i) {
                int num = 0;
                for (int k = j; k < j + i && k <= n; k++) {
                    num = num * 10 + (n + k - n % 10) % 10;
                }
                max = Math.max(max, num);
                minVal = Math.min(minVal, num);
            }
            min = Math.min(min, max - minVal);
        }
        System.out.println(min);
    }

    static int readInt() {
        try {
            return Integer.parseInt(new java.io.BufferedReader(new java.io.InputStreamReader(System.in)).readLine());
        } catch (Exception e) {
            return 0;
        }
    }
}
