#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define MOD 1000000007

long long nCr(long long n, long long r) {
    long long res = 1;
    for (long long i = 1; i <= r; i++) {
        res = (res * (n - i + 1)) % MOD;
        res = (res * pow(i, MOD - 2, MOD)) % MOD;
    }
    return res;
}

int main() {
    long long A, B;
    scanf("%lld %lld", &A, &B);
    long long n = A + B;
    long long ans = 0;
    for (long long i = 0; i <= n - 1; i++) {
        long long j = n - 1 - i;
        long long a = (i + 1) * (i + j + 1) / 2;
        long long b = (j + 1) * (i + j + 1) / 2;
        long long c = nCr(n, i) * nCr(n - i, j) % MOD;
        ans = (ans + c * nCr(a + b - 1, a - 1)) % MOD;
    }
    printf("%lld\n", ans);
    return 0;
}

long long pow(long long a, long long b, long long mod) {
    long long res = 1;
    while (b > 0) {
        if (b & 1) res = (res * a) % mod;
        a = (a * a) % mod;
        b >>= 1;
    }
    return res;
}
