#include <iostream>
#include <vector>
#include <algorithm>
#include <numeric>

using namespace std;

const int MOD = 1e9 + 7;

long long power(long long a, long long b) {
    long long res = 1;
    while (b) {
        if (b & 1) res = res * a % MOD;
        a = a * a % MOD;
        b >>= 1;
    }
    return res;
}

int main() {
    int A, B;
    cin >> A >> B;

    vector<int> fac(A + B + 1);
    fac[0] = 1;
    for (int i = 1; i <= A + B; i++) {
        fac[i] = fac[i - 1] * i % MOD;
    }

    long long ans = 0;
    for (int i = 0; i <= A; i++) {
        int n = A + B - i;
        int k = i;
        long long c = fac[n] * power(fac[k], MOD - 2) % MOD * power(fac[n - k], MOD - 2) % MOD;
        ans = (ans + c) % MOD;
    }

    cout << ans << endl;

    return 0;
}
