import java.io.*;
import java.util.*;

public class Main {
    static final int MOD = 1000000007;
    static final int MAXN = 4005;

    static long[][] C = new long[MAXN][MAXN];
    static long[] fac = new long[MAXN];
    static long[] inv = new long[MAXN];

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);

        fac[0] = 1;
        for (int i = 1; i < MAXN; i++) {
            fac[i] = fac[i - 1] * i % MOD;
        }
        inv[MAXN - 1] = pow(fac[MAXN - 1], MOD - 2);
        for (int i = MAXN - 2; i >= 0; i--) {
            inv[i] = inv[i + 1] * (i + 1) % MOD;
        }
        for (int i = 0; i < MAXN; i++) {
            C[i][0] = 1;
            for (int j = 1; j <= i; j++) {
                C[i][j] = (C[i - 1][j - 1] + C[i - 1][j]) % MOD;
            }
        }

        StringTokenizer st = new StringTokenizer(br.readLine());
        int n = Integer.parseInt(st.nextToken());
        int m = Integer.parseInt(st.nextToken());

        long ans = 0;
        for (int i = 0; i <= n + m; i++) {
            for (int j = 0; j <= n + m; j++) {
                if (i + j <= n + m) {
                    long tmp = C[n + m][i + j] * C[n][i] % MOD * C[m][j] % MOD;
                    tmp = tmp * fac[i] % MOD * fac[j] % MOD * fac[n + m - i - j] % MOD;
                    ans = (ans + tmp) % MOD;
                }
            }
        }
        pw.println(ans);
        pw.close();
    }

    static long pow(long a, long b) {
        long res = 1;
        while (b > 0) {
            if ((b & 1) == 1) {
                res = res * a % MOD;
            }
            a = a * a % MOD;
            b >>= 1;
        }
        return res;
    }
}
