#include <iostream>
#include <algorithm>
#include <string>

using namespace std;

int main() {
    int x, y, z;
    cin >> x >> y >> z;

    string a = "a", b = "b", c = "c";
    string ans = "";

    for (int i = 0; i < x; i++) {
        ans += a;
    }
    for (int i = 0; i < y; i++) {
        ans += b;
    }
    for (int i = 0; i < z; i++) {
        ans += c;
    }

    sort(ans.begin(), ans.end());

    int n = ans.size();
    for (int i = 0; i < n; i++) {
        if (ans[i] == 'a') {
            ans[i] = 'b';
            break;
        }
    }

    for (int i = 0; i < n; i++) {
        if (ans[i] == 'b') {
            ans[i] = 'c';
            break;
        }
    }

    for (int i = 0; i < n; i++) {
        if (ans[i] == 'c') {
            ans[i] = 'a';
            break;
        }
    }

    cout << ans << endl;

    return 0;
}
