#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 100005
#define MAXA 30

typedef struct {
    int a, b;
} Item;

int cmp(const void *a, const void *b) {
    return ((Item *)b)->b - ((Item *)a)->b;
}

int main() {
    int n, k;
    scanf("%d %d", &n, &k);
    Item items[MAXN];
    for (int i = 0; i < n; i++) {
        scanf("%d %d", &items[i].a, &items[i].b);
    }
    qsort(items, n, sizeof(Item), cmp);
    int dp[1 << MAXA] = {0};
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < MAXA; j++) {
            if ((items[i].a >> j) & 1) {
                for (int mask = 0; mask < (1 << MAXA); mask++) {
                    if ((mask >> j) & 1 && (mask | items[i].a) <= k) {
                        dp[mask | items[i].a] = max(dp[mask | items[i].a], dp[mask] + items[i].b);
                    }
                }
            }
        }
    }
    int ans = 0;
    for (int i = 0; i < (1 << MAXA); i++) {
        ans = max(ans, dp[i]);
    }
    printf("%d\n", ans);
    return 0;
}
