#include <iostream>
#include <vector>
#include <algorithm>
#include <numeric>
#include <iomanip>

using namespace std;

struct Point {
    long long x, y;
};

Point operator+(const Point& a, const Point& b) {
    return {a.x + b.x, a.y + b.y};
}

Point operator-(const Point& a, const Point& b) {
    return {a.x - b.x, a.y - b.y};
}

Point operator*(const Point& a, long long b) {
    return {a.x * b, a.y * b};
}

long long cross(const Point& a, const Point& b) {
    return a.x * b.y - a.y * b.x;
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int n;
    cin >> n;

    vector<Point> a(n + 2);
    a[0] = {0, 1};
    a[1] = {1, 0};

    for (int i = 0; i < n; ++i) {
        int x, y, c;
        cin >> x >> y >> c;
        a[i + 2] = {x, y};
    }

    sort(a.begin(), a.end(), [](const Point& a, const Point& b) {
        return cross(a, a[0]) < cross(b, a[0]);
    });

    long long x = 0, y = 0;
    for (int i = 0; i < n + 2; ++i) {
        x = max(x, a[i].x);
        y = max(y, a[i].y);
    }

    for (int i = 0; i < n + 2; ++i) {
        if (cross(a[i], a[i + 1]) < 0) {
            x = min(x, (a[i].x * a[i + 1].y - a[i + 1].x * a[i].y) / cross(a[i], a[0]));
            y = min(y, (a[i].y * a[i + 1].x - a[i + 1].y * a[i].x) / cross(a[i], a[1]));
        }
    }

    cout << fixed << setprecision(9) << x << ' ' << y << '\n';

    return 0;
}
