#include <stdio.h>
#include <stdlib.h>
#include <math.h>

typedef long long ll;

int gcd(int a, int b) {
    if (b == 0) return a;
    return gcd(b, a % b);
}

int main() {
    int q;
    scanf("%d", &q);
    while (q--) {
        int a, m;
        scanf("%d %d", &a, &m);
        if (a == 1) {
            printf("1\n");
            continue;
        }
        int g = gcd(a, m);
        if (m % g != 0) {
            printf("-1\n");
            continue;
        }
        int x = m / g;
        int y = a % x;
        if (y == 0) {
            printf("%d\n", x);
            continue;
        }
        int k = 1;
        while (1) {
            ll val = powll(a, k) % m;
            if (val == y) {
                printf("%lld\n", k);
                break;
            }
            k++;
        }
    }
    return 0;
}

ll powll(ll a, ll b) {
    ll res = 1;
    while (b) {
        if (b & 1) res = res * a;
        a = a * a;
        b >>= 1;
    }
    return res;
}
