#include <iostream>
#include <vector>
#include <cmath>

using namespace std;

typedef long long ll;

ll power(ll a, ll b, ll mod) {
    ll res = 1;
    while (b > 0) {
        if (b & 1) res = (res * a) % mod;
        a = (a * a) % mod;
        b >>= 1;
    }
    return res;
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int q;
    cin >> q;

    while (q--) {
        ll a, m;
        cin >> a >> m;

        if (a == 1) {
            cout << 1 << '\n';
            continue;
        }

        ll k = 1;
        while (power(a, k, m) != k) {
            if (power(a, k, m) > m) break;
            k++;
        }

        if (power(a, k, m) != k) cout << -1 << '\n';
        else cout << k << '\n';
    }

    return 0;
}
